import React, { useState } from 'react';
import { helplineAPI } from '../services/api';
import './Helpline.css';

const Helpline = ({ user }) => {
  const [selectedState, setSelectedState] = useState('');
  const [helplineData, setHelplineData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const states = [
    'Maharashtra',
    'Uttar Pradesh',
    'Delhi',
    'Karnataka',
    'Tamil Nadu',
    'Gujarat',
    'West Bengal',
    'Andhra Pradesh',
    'Telangana',
    'Kerala'
  ];

  const handleStateChange = async (e) => {
    const state = e.target.value;
    setSelectedState(state);
    
    if (state) {
      setLoading(true);
      setError('');
      
      try {
        const response = await helplineAPI.getHelplineNumbers(state);
        setHelplineData(response);
      } catch (error) {
        setError(error.message || 'Failed to fetch helpline numbers. Please try again.');
      } finally {
        setLoading(false);
      }
    } else {
      setHelplineData(null);
    }
  };

  const getHelplineIcon = (category) => {
    const icons = {
      'Emergency': '🚨',
      'Police': '👮',
      'Fire': '🚒',
      'Ambulance': '🚑',
      'Women Helpline': '👩',
      'Child Helpline': '👶',
      'Highway Assistance': '🛣️',
      'Tourist Helpline': '🏛️',
      'Railway Helpline': '🚂'
    };
    return icons[category] || '📞';
  };

  return (
    <div className="container">
      <div className="page-header">
        <h1 className="page-title">Helpline Numbers</h1>
        <p className="page-subtitle">Emergency contact information by state</p>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      <div className="helpline-container">
        {/* State Selection */}
        <div className="state-selection-section">
          <h2 className="section-title">Select Your State</h2>
          <div className="state-selector">
            <select
              value={selectedState}
              onChange={handleStateChange}
              className="form-select"
            >
              <option value="">Choose your state</option>
              {states.map(state => (
                <option key={state} value={state}>{state}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Helpline Numbers */}
        {loading && (
          <div className="loading-state">
            <p>Loading helpline numbers...</p>
          </div>
        )}

        {helplineData && !loading && (
          <div className="helpline-results">
            <h2 className="section-title">
              Emergency Numbers for {helplineData.state}
            </h2>

            {/* National Emergency Numbers */}
            <div className="helpline-section">
              <h3 className="subsection-title">National Emergency Numbers</h3>
              <div className="helpline-grid">
                {Object.entries(helplineData.helpline_numbers.national).map(([category, number]) => (
                  <div key={category} className="helpline-card national">
                    <div className="helpline-icon">
                      {getHelplineIcon(category)}
                    </div>
                    <div className="helpline-info">
                      <h4 className="helpline-category">{category}</h4>
                      <p className="helpline-number">{number}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* State-Specific Numbers */}
            <div className="helpline-section">
              <h3 className="subsection-title">State-Specific Numbers</h3>
              <div className="helpline-grid">
                {Object.entries(helplineData.helpline_numbers.state_specific).map(([category, number]) => (
                  <div key={category} className="helpline-card state">
                    <div className="helpline-icon">
                      {getHelplineIcon(category)}
                    </div>
                    <div className="helpline-info">
                      <h4 className="helpline-category">{category}</h4>
                      <p className="helpline-number">{number}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Emergency Tips */}
            <div className="emergency-tips">
              <h3 className="subsection-title">Emergency Tips</h3>
              <div className="tips-grid">
                <div className="tip-card">
                  <div className="tip-icon">📞</div>
                  <h4>Call 112</h4>
                  <p>For any emergency situation, dial 112 for immediate assistance.</p>
                </div>
                <div className="tip-card">
                  <div className="tip-icon">🚨</div>
                  <h4>Stay Calm</h4>
                  <p>Remain calm and provide clear information when calling emergency services.</p>
                </div>
                <div className="tip-card">
                  <div className="tip-icon">📍</div>
                  <h4>Location Details</h4>
                  <p>Always provide your exact location and landmarks when reporting emergencies.</p>
                </div>
                <div className="tip-card">
                  <div className="tip-icon">📱</div>
                  <h4>Save Numbers</h4>
                  <p>Save important emergency numbers in your phone for quick access.</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {!selectedState && !loading && (
          <div className="empty-state">
            <div className="empty-icon">📞</div>
            <h3 className="empty-title">Select a State</h3>
            <p className="empty-text">
              Choose your state from the dropdown above to view emergency helpline numbers.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Helpline;
