import React, { useState } from 'react';
import { helplineAPI } from '../services/api';
import './Helpline.css';

const Helpline = ({ user }) => {
  const [selectedState, setSelectedState] = useState('');
  const [selectedIssue, setSelectedIssue] = useState('');
  const [helplineData, setHelplineData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const states = [
    'Maharashtra',
    'Uttar Pradesh',
    'Delhi',
    'Karnataka',
    'Tamil Nadu',
    'Gujarat',
    'West Bengal',
    'Andhra Pradesh',
    'Telangana',
    'Kerala'
  ];

  const issues = [
    'Women Help',
    'Child Help',
    'Transport',
    'Water',
    'Electricity',
    'Health'
  ];

  const handleStateChange = async (e) => {
    const state = e.target.value;
    setSelectedState(state);
    
    if (state) {
      setLoading(true);
      setError('');
      
      try {
        const response = await helplineAPI.getHelplineNumbers(state);
        setHelplineData(response);
      } catch (error) {
        setError(error.message || 'Failed to fetch helpline numbers. Please try again.');
      } finally {
        setLoading(false);
      }
    } else {
      setHelplineData(null);
    }
  };

  const getHelplineIcon = (service) => {
    const icons = {
      'Emergency Response Support System (ERSS)': 'üö®',
      'National Women Helpline': 'üë©',
      'Childline India': 'üë∂',
      'National Health Helpline': 'üè•',
      'National Senior Citizen Helpline (Elder Line)': 'üë¥',
      'KIRAN Mental Health Rehabilitation Helpline': 'üß†',
      'National Cyber Crime Reporting Portal': 'üíª',
      'National Consumer Helpline': 'üõí',
      'Kisan Call Centre (for Farmers)': 'üåæ',
      'CPGRAMS': 'üìã',
      'Rail Madad': 'üöÇ',
      'Ayushman Bharat (PMJAY)': 'üè•',
      'E-SHRAM Portal': 'üë∑',
      'UP CM Helpline (Jansunwai - Samadhan)': 'üèõÔ∏è',
      'Women and Child Power Line (UP Police)': 'üëÆ',
      'UP Power Corporation Ltd. (Electricity)': '‚ö°',
      'UP State Road Transport Corporation (UPSRTC)': 'üöå',
      'CM Window (Jan Sahayak)': 'üèõÔ∏è',
      'Electricity (UHBVN)': '‚ö°',
      'Police / Emergency': 'üëÆ',
      'Women Helpline': 'üë©',
      'Ration / Food Distribution': 'üçû',
      'Electricity (MSEDCL)': '‚ö°',
      'Ration / Food Supply': 'üçû',
      'Electricity (PSPCL)': '‚ö°',
      'Rajasthan Sampark': 'üèõÔ∏è',
      'Water Supply (PHED)': 'üíß',
      'Women/Child Protection': 'üë©‚Äçüëß‚Äçüë¶',
      'Emergency': 'üö®',
      'Police': 'üëÆ',
      'Fire': 'üöí',
      'Ambulance': 'üöë',
      'Child Helpline': 'üë∂',
      'Highway Assistance': 'üõ£Ô∏è',
      'Tourist Helpline': 'üèõÔ∏è',
      'Railway Helpline': 'üöÇ'
    };
    return icons[service] || 'üìû';
  };

  return (
    <div className="container">
      <div className="page-header">
        <h1 className="page-title">Helpline Numbers</h1>
        <p className="page-subtitle">Emergency contact information by state</p>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      <div className="helpline-container">
        {/* State Selection */}
        <div className="state-selection-section">
          <h2 className="section-title">Select Your State</h2>
          <div className="state-selector">
            <select
              value={selectedState}
              onChange={handleStateChange}
              className="form-select"
            >
              <option value="">Choose your state</option>
              {states.map(state => (
                <option key={state} value={state}>{state}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Helpline Numbers */}
        {loading && (
          <div className="loading-state">
            <p>Loading helpline numbers...</p>
          </div>
        )}

        {helplineData && !loading && (
          <div className="helpline-results">
            <h2 className="section-title">
              Helpline Numbers for {selectedState}
            </h2>

            {/* Central Helplines */}
            <div className="helpline-section">
              <h3 className="subsection-title">National Helplines</h3>
              <div className="helpline-grid">
                {helplineData.helplines.filter(helpline => helpline.level === 'Central').map((helpline, index) => (
                  <div key={index} className="helpline-card national">
                    <div className="helpline-icon">
                      {getHelplineIcon(helpline.service)}
                    </div>
                    <div className="helpline-info">
                      <h4 className="helpline-category">{helpline.service}</h4>
                      <p className="helpline-number">{helpline.number}</p>
                      {helpline.notes && (
                        <p className="helpline-notes">{helpline.notes}</p>
                      )}
                      <div className="helpline-links">
                        {helpline.website && (
                          <a 
                            href={helpline.website} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="helpline-link"
                          >
                            üåê Website
                          </a>
                        )}
                        {helpline.email && (
                          <a 
                            href={`mailto:${helpline.email}`}
                            className="helpline-link"
                          >
                            üìß Email
                          </a>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* State-Specific Helplines */}
            <div className="helpline-section">
              <h3 className="subsection-title">State-Specific Helplines</h3>
              <div className="helpline-grid">
                {helplineData.helplines.filter(helpline => helpline.level === 'State' && helpline.state === selectedState).map((helpline, index) => (
                  <div key={index} className="helpline-card state">
                    <div className="helpline-icon">
                      {getHelplineIcon(helpline.service)}
                    </div>
                    <div className="helpline-info">
                      <h4 className="helpline-category">{helpline.service}</h4>
                      <p className="helpline-number">{helpline.number}</p>
                      {helpline.notes && (
                        <p className="helpline-notes">{helpline.notes}</p>
                      )}
                      <div className="helpline-links">
                        {helpline.website && (
                          <a 
                            href={helpline.website} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="helpline-link"
                          >
                            üåê Website
                          </a>
                        )}
                        {helpline.email && (
                          <a 
                            href={`mailto:${helpline.email}`}
                            className="helpline-link"
                          >
                            üìß Email
                          </a>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Emergency Tips */}
            <div className="emergency-tips">
              <h3 className="subsection-title">Emergency Tips</h3>
              <div className="tips-grid">
                <div className="tip-card">
                  <div className="tip-icon">üìû</div>
                  <h4>Call 112</h4>
                  <p>For any emergency situation, dial 112 for immediate assistance.</p>
                </div>
                <div className="tip-card">
                  <div className="tip-icon">üö®</div>
                  <h4>Stay Calm</h4>
                  <p>Remain calm and provide clear information when calling emergency services.</p>
                </div>
                <div className="tip-card">
                  <div className="tip-icon">üìç</div>
                  <h4>Location Details</h4>
                  <p>Always provide your exact location and landmarks when reporting emergencies.</p>
                </div>
                <div className="tip-card">
                  <div className="tip-icon">üì±</div>
                  <h4>Save Numbers</h4>
                  <p>Save important emergency numbers in your phone for quick access.</p>
                </div>
              </div>
            </div>

            {/* Disclaimer */}
            <div className="disclaimer-section">
              <h3 className="subsection-title">Important Notice</h3>
              <div className="disclaimer-content">
                <p><strong>Disclaimer:</strong> Helpline numbers and web links are subject to change. For the most critical emergencies, please dial <strong>112</strong>. Most helplines listed are toll-free, but please check with your service provider.</p>
                <p>This information is provided for public service and should be used responsibly. Always verify the current status of helpline numbers before use.</p>
              </div>
            </div>
          </div>
        )}

        {!selectedState && !loading && (
          <div className="empty-state">
            <div className="empty-icon">üìû</div>
            <h3 className="empty-title">Select a State</h3>
            <p className="empty-text">
              Choose your state from the dropdown above to view emergency helpline numbers.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Helpline;
