import React, { useState, useRef, useEffect } from 'react';
import { voiceAPI } from '../services/api';
import './VoiceToText.css';

const VoiceToText = ({ onVoiceResult, userState, userLanguage }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [voiceText, setVoiceText] = useState('');
  const [detectedLanguage, setDetectedLanguage] = useState('');
  const [languageConfidence, setLanguageConfidence] = useState(0);
  const [suggestedRegion, setSuggestedRegion] = useState('');
  const [helplineNumbers, setHelplineNumbers] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState('');
  const [supportedLanguages, setSupportedLanguages] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('auto');
  const [recognition, setRecognition] = useState(null);

  const recognitionRef = useRef(null);

  useEffect(() => {
    // Load supported languages
    loadSupportedLanguages();
    
    // Initialize speech recognition
    initializeSpeechRecognition();
  }, []);

  const loadSupportedLanguages = async () => {
    try {
      const response = await voiceAPI.getLanguageSupport();
      if (response.success) {
        setSupportedLanguages(response.supported_languages);
      }
    } catch (error) {
      console.error('Error loading supported languages:', error);
    }
  };

  const initializeSpeechRecognition = () => {
    if (!window.SpeechRecognition && !window.webkitSpeechRecognition) {
      setError('Speech recognition is not supported in this browser');
      return;
    }

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognitionInstance = new SpeechRecognition();
    
    recognitionInstance.continuous = true;
    recognitionInstance.interimResults = true;
    recognitionInstance.lang = selectedLanguage === 'auto' ? 'hi-IN' : selectedLanguage;
    
    recognitionInstance.onstart = () => {
      setIsRecording(true);
      setError('');
    };

    recognitionInstance.onresult = (event) => {
      let finalTranscript = '';
      let interimTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript;
        } else {
          interimTranscript += transcript;
        }
      }

      if (finalTranscript) {
        setVoiceText(finalTranscript);
        processVoiceInput(finalTranscript);
      }
    };

    recognitionInstance.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      setError(`Voice recognition error: ${event.error}`);
      setIsRecording(false);
    };

    recognitionInstance.onend = () => {
      setIsRecording(false);
    };

    setRecognition(recognitionInstance);
  };

  const startRecording = () => {
    if (!recognition) {
      setError('Speech recognition not initialized');
      return;
    }

    setVoiceText('');
    setDetectedLanguage('');
    setSuggestedRegion('');
    setHelplineNumbers([]);
    setError('');

    try {
      recognition.start();
    } catch (error) {
      setError('Failed to start recording. Please try again.');
    }
  };

  const stopRecording = () => {
    if (recognition) {
      recognition.stop();
    }
  };

  const processVoiceInput = async (text) => {
    if (!text.trim()) return;

    setIsProcessing(true);
    setError('');

    try {
      const response = await voiceAPI.voiceToText({
        text: text,
        state: userState,
        language: userLanguage || selectedLanguage
      });

      if (response.success) {
        setDetectedLanguage(response.language_detection.language);
        setLanguageConfidence(response.language_detection.confidence);
        setSuggestedRegion(response.region_data.primary_states[0]);
        setHelplineNumbers(response.state_helplines);
        
        // Call parent callback with results
        if (onVoiceResult) {
          onVoiceResult({
            text: text,
            detectedLanguage: response.language_detection.language,
            suggestedRegion: response.region_data.primary_states[0],
            helplineNumbers: response.state_helplines,
            aiAnalysis: response.ai_analysis,
            multilingualResponse: response.multilingual_response
          });
        }
      }
    } catch (error) {
      setError('Failed to process voice input. Please try again.');
      console.error('Voice processing error:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleLanguageChange = (e) => {
    const newLanguage = e.target.value;
    setSelectedLanguage(newLanguage);
    
    if (recognition) {
      recognition.lang = newLanguage === 'auto' ? 'hi-IN' : newLanguage;
    }
  };

  const getLanguageDisplayName = (languageCode) => {
    const language = supportedLanguages.find(lang => lang.language_code === languageCode);
    return language ? language.native_name : languageCode;
  };

  const formatHelplineNumber = (service, number) => {
    const serviceNames = {
      'emergency': 'Emergency',
      'ambulance': 'Ambulance',
      'police': 'Police',
      'fire': 'Fire',
      'women_helpline': 'Women Helpline',
      'cm_helpline': 'CM Helpline',
      'electricity': 'Electricity',
      'transport': 'Transport',
      'food_supply': 'Food Supply',
      'water_supply': 'Water Supply'
    };
    
    return {
      service: serviceNames[service] || service.replace('_', ' ').toUpperCase(),
      number: number
    };
  };

  return (
    <div className="voice-to-text-container">
      <div className="voice-controls">
        <div className="language-selector">
          <label htmlFor="language-select">Select Language:</label>
          <select 
            id="language-select" 
            value={selectedLanguage} 
            onChange={handleLanguageChange}
            disabled={isRecording}
          >
            <option value="auto">Auto Detect</option>
            {supportedLanguages.map((lang) => (
              <option key={lang.language_code} value={lang.language_code}>
                {lang.native_name} ({lang.language})
              </option>
            ))}
          </select>
        </div>

        <div className="recording-controls">
          <button
            className={`record-button ${isRecording ? 'recording' : ''}`}
            onClick={isRecording ? stopRecording : startRecording}
            disabled={isProcessing}
          >
            {isRecording ? (
              <>
                <span className="recording-indicator"></span>
                Stop Recording
              </>
            ) : (
              <>
                <span className="microphone-icon">ðŸŽ¤</span>
                Start Recording
              </>
            )}
          </button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {isProcessing && (
        <div className="processing-message">
          Processing voice input...
        </div>
      )}

      {voiceText && (
        <div className="voice-results">
          <div className="voice-text-section">
            <h3>Voice Input:</h3>
            <p className="voice-text">{voiceText}</p>
          </div>

          {detectedLanguage && (
            <div className="language-detection-section">
              <h3>Language Detection:</h3>
              <p>
                <strong>Language:</strong> {getLanguageDisplayName(detectedLanguage)} 
                ({Math.round(languageConfidence * 100)}% confidence)
              </p>
              {suggestedRegion && (
                <p><strong>Suggested Region:</strong> {suggestedRegion}</p>
              )}
            </div>
          )}

          {helplineNumbers && Object.keys(helplineNumbers).length > 0 && (
            <div className="helpline-section">
              <h3>Regional Helpline Numbers:</h3>
              <div className="helpline-grid">
                {Object.entries(helplineNumbers).map(([service, number]) => {
                  const formatted = formatHelplineNumber(service, number);
                  return (
                    <div key={service} className="helpline-item">
                      <div className="helpline-service">{formatted.service}</div>
                      <div className="helpline-number">{formatted.number}</div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      )}

      <div className="voice-instructions">
        <h4>How to use:</h4>
        <ul>
          <li>Select your preferred language or use "Auto Detect"</li>
          <li>Click "Start Recording" and speak your emergency or issue</li>
          <li>The system will automatically detect your language and region</li>
          <li>Relevant helpline numbers will be provided based on your location</li>
          <li>You can speak in any supported Indian language</li>
        </ul>
      </div>
    </div>
  );
};

export default VoiceToText;
